import React, { Component } from 'react';
import { BrowserRouter as Router, Route } from 'react-router-dom';
import './App.css';

import Home from './components/Home';
import Navbar from "./components/Navbar";
import CreateProject from './components/CreateProject';
import ShowProjectList from './components/ShowProjectList';
import ShowProjectDetails from './components/ShowProjectDetails';
import UpdateProject from './components/UpdateProject';

class App extends Component {
  render() {
    return (
      <Router>
        <div>
          <Navbar/>
          <Route exact path='/' component={Home} />
          <Route path='/projects' component={ShowProjectList} />
          <Route path='/create-project' component={CreateProject} />
          <Route path='/edit-project/:id' component={UpdateProject} />
          <Route path='/show-project/:id' component={ShowProjectDetails} />
          <Route path='/register' component={Register()} />
        </div>
      </Router>
    );
  }
}

function Register() {
  const history = useHistory();

    async function handleRegister(e) {
        e.preventDefault();

        const form = e.target;
        const user = {
            email: form[0].value,
            email: form[1].value
        }

        fetch("/register", {
            method: "POST",
            headers: {
                "Content-type": "application/json"
            },
            body: JSON.stringify(user)
        })
    }

    useEffect(() => {
        fetch("/isUserAuth", {
            headers: {
                "x-access-token": localStorage.getItem("token")
            }
        })
        .then(res => res.json())
        .then(data => data.isLoggedIn ? history.push("/dashboard"): null)
    }, [])

    return (
        <form onSubmit={event => handleRegister(event)}>
            <input required type="email" />
            <input required type="password" />
            <input type="submit" value="Register" />
        </form>
    )
}

export default App;
